example.code:
public class Avaliador
{
    public int Id { get; set; }
    public DateTime DataTreinamento { get; set; }
    public int IdPessoa { get; set; }
    public Corporativo.Models.PessoaFisica PessoaFisica { get; set; }
}

var avaliadores = new List<Avaliador>
{
    new Avaliador { Id = 1, IdPessoa = 1, DataTreinamento = new DateTime(2014, 3, 2) }
};
var cliente = new Corporativo.API.Cliente(new Corporativo.Models.Credencial("fulano@inmetro.gov.br", "123456"));
cliente.PessoasFisicas.Get(avaliadores, "IdPessoa", "PessoaFisica");

Console.Write(avaliadores[0].PessoaFisica.Nome);
